public class YahooSRV_LISTPacketTest extends TestCase {

	/**
	 * Constructor for YahooSRV_LISTPacketTest.
	 * @param arg0
	 */
	public YahooSRV_LISTPacketTest(String arg0) {
		super(arg0);
	}
	
	public static Test suite() {
		return new TestSuite(YahooSRV_LISTPacketTest.class);
	}

	public void testYahooSRV_LISTPacket() {
		YahooSRV_LISTPacket srv_list = new YahooSRV_LISTPacket("");
	
		YahooSRV_LISTPacket srv_list2 = new YahooSRV_LISTPacket("");
		YahooSRV_LISTPacket srv_list3 = new YahooSRV_LISTPacket("");
		
		Vector v = srv_list.getList();
		Vector v2 = srv_list2.getList();
		Vector v3 = srv_list3.getList();

		assertTrue(v3.size() == 1);
		assertTrue(v2.size() == 2);
		
		assertTrue(((YahooContact)v3.elementAt(0)).getLogin().equals("bacardibart"));
		
		/*for(int i = 0; i < v2.size(); i++) {
			System.out.println((String)v2.elementAt(i));
		}*/
		
		/*assertTrue(v.size() == 2);*/
	}

	public void testGetKindOfPacket() {
		YahooSRV_LISTPacket srv_list = new YahooSRV_LISTPacket("");
		YahooSRV_LISTPacket srv_list3 = new YahooSRV_LISTPacket("");
		
		assertTrue(srv_list.getKindOfPacket().equals("SRV_LIST"));
		assertTrue(srv_list.getKindOfPacket().equals("SRV_LIST"));
	}

	public void testGetContentHex() {
		YahooSRV_LISTPacket srv_list = new YahooSRV_LISTPacket("");
		YahooSRV_LISTPacket srv_list3 = new YahooSRV_LISTPacket("");
		
		assertTrue(srv_list.getContentHex().equals(""));
		assertTrue(srv_list3.getContentHex().equals(""));
	}

	public void testGetLength() {
		YahooSRV_LISTPacket srv_list = new YahooSRV_LISTPacket("");
		YahooSRV_LISTPacket srv_list3 = new YahooSRV_LISTPacket("");
		
		assertTrue(srv_list3.getLength() == 637);
		
	}
	
	public static void main (String[] args) {
		junit.textui.TestRunner.run(suite());
	}

}
